/* LeanCloud Realtime SDK 最小化替代版本 */
window.Realtime = function(options) {
  this.createIMClient = function(clientId) {
    return Promise.resolve({
      on: function() {},
      getConversation: function() {
        return Promise.resolve({
          send: function() { return Promise.resolve(); },
          on: function() {}
        });
      },
      createConversation: function() {
        return Promise.resolve({
          send: function() { return Promise.resolve(); },
          on: function() {}
        });
      }
    });
  };
};

// 确保AV存在且有Realtime属性
window.AV = window.AV || {};
window.AV.Realtime = window.AV.Realtime || {
  TextMessage: function(text) { this.text = text; },
  FileMessage: function(file) { this.file = file; }
};
console.log('本地 Realtime SDK 已加载');
