/* LeanCloud Realtime Typed Messages 最小化替代版本 */
// 确保全局对象存在
window.AV = window.AV || {};
window.AV.Realtime = window.AV.Realtime || {};

// 定义标准消息类型
window.AV.Realtime.TextMessage = function(text) {
  this.text = text || '';
  this.getAttributes = function() { return {}; };
  this._lctype = -1;
};

window.AV.Realtime.FileMessage = function(file) {
  this.file = file || {};
  this.getAttributes = function() { return {}; };
  this._lcfile = { url: '', name: '' };
  this._lctype = -2;
};

console.log('本地 Typed Messages SDK 已加载');
